const booksPerPage = 15;
let currentPage = 1;
let currentCategory = 'all';

function updateBooksDisplay() {
    const books = document.querySelectorAll('.book');
    let visibleCount = 0;
    let totalVisible = 0;

    // 先计算可见书籍总数
    books.forEach(book => {
        if (currentCategory === 'all' || book.dataset.category === currentCategory) {
            totalVisible++;
        }
    });

    // 更新分页信息
    const totalPages = Math.max(1, Math.ceil(totalVisible / booksPerPage));

    // 更新书籍显示
    books.forEach((book, index) => {
        const showBook = (currentCategory === 'all' || book.dataset.category === currentCategory);
        const showOnPage = showBook && 
                            index >= (currentPage - 1) * booksPerPage && 
                            index < currentPage * booksPerPage;

        book.style.display = showOnPage ? 'block' : 'none';

        if (showBook && showOnPage) {
            visibleCount++;
        }
    });

    // 更新分页按钮
    const pagination = document.querySelector('.pagination');
    pagination.innerHTML = '';

    // 添加上一页按钮
    if (currentPage > 1) {
        const prevLink = document.createElement('a');
        prevLink.className = 'extend prev';
        prevLink.href = 'javascript:;';
    prevLink.innerHTML = '';
    prevLink.addEventListener('click', () => {
        currentPage--;
        updateBooksDisplay();
    });
    pagination.appendChild(prevLink);
}

    // 添加页码按钮
for (let i = 1; i  {
        currentPage = i;
        updateBooksDisplay();
    });
    pagination.appendChild(pageNum);
}

    // 添加下一页按钮
if (currentPage < totalPages) {
    const nextLink = document.createElement('a');
    nextLink.className = 'extend next';
    nextLink.href = 'javascript:;';
    nextLink.innerHTML = '';
    nextLink.addEventListener('click', () => {
        currentPage++;
        updateBooksDisplay();
    });
    pagination.appendChild(nextLink);
}

    // 滚动到书架顶部
document.querySelector('.controls').scrollIntoView({
    behavior: 'smooth'
});
}

// 分类按钮点击事件
document.querySelectorAll('.category-btn').forEach(btn => {
    btn.addEventListener('click', () => {
        document.querySelectorAll('.category-btn').forEach(b => b.classList.remove('active'));
        btn.classList.add('active');
        currentCategory = btn.dataset.category;
        currentPage = 1;
        updateBooksDisplay();
    });
});


// 初始化显示
updateBooksDisplay();
